    def load_picture(self):
        file_dialog = QtWidgets.QFileDialog()
        file_dialog.setWindowTitle("Select Image")
        file_dialog.setFileMode(QtWidgets.QFileDialog.FileMode.ExistingFile)
        file_dialog.setNameFilter("Images (*.png *.xpm *.jpg *.bmp)")
        
        if file_dialog.exec() == QtWidgets.QFileDialog.DialogCode.Accepted:
            file_path = file_dialog.selectedFiles()[0]
            file_name = file_path.split("/")[-1]
            self.label_file_name_show.setText(file_name)
            
            # Convert the image to binary file
            with open(file_path, 'rb') as image_file:
                image_data = image_file.read()
            self.temp_blob_variable = bytearray(image_data)
            
    def sort_table_warehouse_products(self, logical_index):
        # mijenja redosljed sortiranja
        
        # self.table_proizvodi_array is a list, that contains tuples for its element
        # convert tuples to the list for easy manipulation of the list
        self.new_array_table_warehouse_products = [list(x) for x in self.table_show_warehouse_products_array]
        
        # checks for the last element in the inner array, if it is None, it sets it to False, otherway True
        # also checks if it is already set to True or False, if it is, do nothing
        for inner_array in self.new_array_table_warehouse_products:
            last_element = inner_array[-1]
            if last_element is None:
                inner_array[-1] = "False"
            elif last_element == "True" or last_element == "False":
                # Do nothing, leave it unchanged if set to True or False
                pass
            elif last_element:
                inner_array[-1] = "True"          
        
        # replace old list with the new one
        self.table_show_warehouse_products_array = self.new_array_table_warehouse_products
        
        # sort the list by the logical index (Logical Index is the number of the column in the table)
        self.sort_order = QtCore.Qt.SortOrder.DescendingOrder if self.sort_order == QtCore.Qt.SortOrder.AscendingOrder else QtCore.Qt.SortOrder.AscendingOrder
        self.table_show_warehouse_products_array.sort(key = lambda x: x[logical_index], reverse = self.sort_order == QtCore.Qt.SortOrder.DescendingOrder)
        # update table with new values on the GUI
        self.update_table_warehouse_products()

